def sequence():    """    This function takes lines of integers as input and then determines    how many elements of the input are sorted, either in ascending    or descending.    :return: The number of the ints which are in order    """    ints_arr = []  # integers given in the order    flag = True    while flag:        int_input = input()        int_input = int_input.strip(" ").split()        if int_input != "\n":            for an_int in int_input:                ints_arr.append(int(an_int))        if ints_arr == sorted(ints_arr) or ints_arr == sorted(ints_arr, reverse=True):            flag = True        else:            flag = False    ret_val = []    for i in range(len(ints_arr)):        if ints_arr[:i] == sorted(ints_arr[:i]) or \                ints_arr[:i] == sorted(ints_arr[:i], reverse=True):            ret_val = ints_arr[:i]            continue        else:            break    return len(ret_val)def main():    print(sequence())if __name__ == '__main__':    main()