def swap(str_input):    """    :param str_input:    :return: None    This function swaps the place of the two halves    of the string given as a parameter.    """    str_input = str_input.strip(" \n ")    middle_index = len(str_input) // 2    if len(str_input) % 2 == 0:        str_input = str_input[middle_index:] + str_input[:middle_index]    else:        str_input = str_input[middle_index+1:] + \                    str_input[middle_index] + \                    str_input[:middle_index]    print(str_input)def main():    str_input = input("Please give a string to swap: ")    swap(str_input)if __name__ == '__main__':    main()