""" File: music_compare.py    Author: Otabek Abduraimov    Purpose: This program imports all the functions    from the utils.py and prints out the    similarity report in a nice format.    Course #: CS 120, Fall 2021"""from utils import *def main():    obj = open(input("file: "))    n = int(input("n: "))    print("--- SONG LIST ---")    list_of_num_info_melody = read_file(obj)    # print(list_of_num_info_melody)    for item in list_of_num_info_melody:        print(f'id={item[0]} info="{item[1]}" notes={item[2]}')    print()    print("--- COMPARISONS ---")    first_song_id = list_of_num_info_melody[0][0]    first_song_melody = list_of_num_info_melody[0][2]    max_sim_ratio = 0    max_sim_ratio_song = None    i = 1    while i < len(list_of_num_info_melody):        song_melody = list_of_num_info_melody[i][2]        sim_ratio = compare_melodies(first_song_melody, song_melody, n)        if sim_ratio > max_sim_ratio:            max_sim_ratio = sim_ratio            max_sim_ratio_song = list_of_num_info_melody[i]        print(f"id_a={first_song_id} id_b={list_of_num_info_melody[i][0]} similarity={sim_ratio}")        i += 1    print()    print("--- RESULT ---")    song_name = list_of_num_info_melody[0][1]    other_song_name = max_sim_ratio_song[1]    other_song_id = max_sim_ratio_song[0]    print(f"Most similar songs:\n"          f"  {song_name}\n"          f"  {other_song_name}")    print()    print(f"  ids: {first_song_id}\n"          f"  ids: {other_song_id}")    print()    print("  Melodies:")    first_song_melody = list_of_num_info_melody[0][2]    second_song_melody = max_sim_ratio_song[2]    print(f"    {' '.join(first_song_melody)}")    print(f"    {' '.join(second_song_melody)}")    print()    print("  Set 1")    for elem in create_set(list_of_num_info_melody[0][2], n):        print(f"    {elem}")    print()    print("  Set 2")    for elem in create_set(max_sim_ratio_song[2], n):        print(f"    {elem}")if __name__ == '__main__':    main()